  useEffect(() => {
    console.log("Component güncellendi")

    const interval = setInterval(()=>{
        setNumber((n)=> n + 1)
    },1000)

    return () => console.log("Component unmount edıldı.")
  },[])

  useEffect(() => {
    console.log("number state güncellendi")
  },[number])

//   useEffect(() => {
